import { Component } from '@angular/core';
import { ProductService } from 'src/app/login/services/product.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent {
  data_storage: any[] = []; // Ensure this is defined

  constructor(private productService: ProductService) {}

  // Method to receive data from AddProductComponent
  recivedData(newDataStorage: any) {
    this.data_storage.push(newDataStorage);
    localStorage.setItem('cart_data', JSON.stringify(this.data_storage));
    console.log('Updated data storage:', this.data_storage); // Debugging
  }
}
