import { Router } from '@angular/router';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ProductService } from 'src/app/login/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {

  @Input() editProd: any[] = [];
  @Output() dataStorageUpdated = new EventEmitter<any>();

  addProductData: any = {};
  data_storage: any[] = [];
  editproduct: number = 0;

  constructor(private router: Router, private productservice: ProductService) {
    this.checkLoginStatus();
  }

  checkLoginStatus() {
    if (localStorage.getItem('isLoggedIn') !== 'true') {
      this.router.navigate(['/login']);
    }
  }

  ngOnInit(): void {
    this.getProductData(); 
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      this.addProductData.image = reader.result as string;
      const base64String = event.target.result;
      console.log(base64String);
    };

    reader.readAsDataURL(file);
  }

  onSubmit() {
    let productData = {
      id: this.addProductData.id,
      name: this.addProductData.name,
      price: this.addProductData.price,
      description: this.addProductData.description,
      image: this.addProductData.image,
      quantity: 0
    };

    if (productData) { // Check if productData is not null or undefined
      this.dataStorageUpdated.emit(productData);
    }
  }

  // Move getProductData outside of onSubmit
  getProductData() {
    this.productservice.editProductData$.subscribe((data) => {
      this.editproduct = data.length;

      if (data.length > 0) {
        const receivedData = data[0];
        this.addProductData.id = receivedData.id;
        this.addProductData.name = receivedData.name;
        this.addProductData.price = receivedData.price;
        this.addProductData.description = receivedData.description;
        console.log('Received data in second component:', this.addProductData);
      }
    });
  }
}


<div class="register-form">

    <form  (ngSubmit)="onSubmit()">

         <div class="form-group">

             <input type="number" [(ngModel)]="addProductData.id" class="form-control"  placeholder="Id" name="id">

         </div>

         <div class="form-group">

             <input type="text" class="form-control" [(ngModel)]="addProductData.name"  placeholder="Name" name="name">


         </div>

         <div class="form-group">

             <input type="number" class="form-control" [(ngModel)]="addProductData.price"  placeholder="Price" name="price">

         </div>

         <div class="form-group">

             <div class="form-floating">
                 <textarea class="form-control" name="description" [(ngModel)]="addProductData.description" placeholder="Leave a comment here" id="floatingTextarea2" style="height: 100px"></textarea>
                 <label for="floatingTextarea2">Description</label>
             </div>

         </div>

         <div class="form-group">

           <input class="form-control" name="image" (change)="onFileChange($event)" type="file" id="formFile">

         </div>


         <div class="form-group">
             <button type="submit" class=>Add Product</button>
         </div>


     </form>

 </div>
