<div class="register-form">
    <form (ngSubmit)="onSubmit()">
      <div class="form-group">
        <input type="number" [(ngModel)]="addProductData.id" class="form-control" placeholder="Id" name="id">
      </div>

      <div class="form-group">
        <input type="text" class="form-control" [(ngModel)]="addProductData.name" placeholder="Name" name="name">
      </div>

      <div class="form-group">
        <input type="number" class="form-control" [(ngModel)]="addProductData.price" placeholder="Price" name="price">
      </div>

      <div class="form-group">
        <textarea class="form-control" [(ngModel)]="addProductData.description" placeholder="Description" name="description" rows="3"></textarea>
      </div>

      <div class="form-group">
        <input class="form-control" type="file" (change)="onFileChange($event)" />
      </div>

      <div class="form-group">
        <button type="submit" class="btn btn-primary">Add Product</button>
      </div>
    </form>
  </div>
