import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { HttpClientModule } from '@angular/common/http';
import { FormsModule } from '@angular/forms'; // For two-way binding with ngModel

import { AppComponent } from './app.component';
import { LoginComponent } from './login/login.component';
import { MainComponent } from './main/main.component';
import { ProductListComponent } from './main/product-list/product-list.component';
import { ApproutingModule } from './approuting/app.routing.module'; // Make sure this path is correct

@NgModule({
  declarations: [
    AppComponent,
    LoginComponent,
    MainComponent,
    ProductListComponent,
  ],
  imports: [
    BrowserModule,
    HttpClientModule,
    FormsModule,
    ApproutingModule, // This includes RouterModule, so no need to import RouterModule separately
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
