import { Router } from '@angular/router';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ProductService } from 'src/app/login/services/product.service';


@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {

  @Input() editProd: any[] = [];

  @Output() dataStorageUpdated = new EventEmitter<any>();


  addProductData:any = [];

  data_storage: any[] = [];

  editproduct:number = 0;
constructor(private router : Router, private productservice:ProductService)


{this.checkLoginStatus();}
checkLoginStatus(){
  if (localStorage.getItem('isLoggedIn') !== 'true'){
    this.router.navigate(['/login']);
  }
}
ngOnInit(): void {
  this.getProductData();
}



onFileChange(event: any) {
  const file = event.target.files[0];
  const reader = new FileReader();

  reader.onload = () => {

    this.addCartData.image = reader.result as string;

    const base64String = event.target.result;
    console.log(base64String)
  };


  reader.readAsDataURL(file);
}


onSubmit(){

  let productData = {
    id: this.addCartData.id,
    name: this.addCartData.name,
    price: this.addCartData.price,
    description: this.addCartData.description,
    image: this.addCartData.image,
    quantity: 0
  }

   if(productData !== null){
    this.dataStorageUpdated.emit(productData);
  }

  getProductData(){
    this.productservice.editProductData$.subscribe((data) =>{

      this.editproduct = data.length;

      if (data.length > 0) {
        const receivedData = data[0];
        this.addCartData.id = receivedData.id;
        this.addCartData.name = receivedData.name;
        this.addCartData.price = receivedData.price;
        this.addCartData.description = receivedData.description;
        console.log('Received data in second component:', this.addCartData);
      }
    })
  }
}
}


<div class="register-form">

    <form  (ngSubmit)="onSubmit()">

         <div class="form-group">

             <input type="number" [(ngModel)]="addCartData.id" class="form-control"  placeholder="Id" name="id">

         </div>

         <div class="form-group">

             <input type="text" class="form-control" [(ngModel)]="addCartData.name"  placeholder="Name" name="name">


         </div>

         <div class="form-group">

             <input type="number" class="form-control" [(ngModel)]="addCartData.price"  placeholder="Price" name="price">

         </div>

         <div class="form-group">

             <div class="form-floating">
                 <textarea class="form-control" name="description" [(ngModel)]="addCartData.description" placeholder="Leave a comment here" id="floatingTextarea2" style="height: 100px"></textarea>
                 <label for="floatingTextarea2">Description</label>
             </div>

         </div>

         <div class="form-group">

           <input class="form-control" name="image" (change)="onFileChange($event)" type="file" id="formFile">

         </div>


         <div class="form-group">
             <button type="submit" class="btn btn-primary">Add Product</button>
         </div>


     </form>

 </div>
