import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Router } from '@angular/router';
import { ProductService } from 'src/app/login/services/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @Input() editProd: any[] = [];
  @Output() dataStorageUpdated = new EventEmitter<any>();

  addProductData: any = { 
    id: null,
    name: '',
    price: null,
    description: '',
    image: ''
  };

  editproduct: number = 0;

  constructor(private router: Router, private productservice: ProductService) {
    this.checkLoginStatus();
  }

  checkLoginStatus() {
    if (localStorage.getItem('isLoggedIn') !== 'true') {
      this.router.navigate(['/login']);
    }
  }

  ngOnInit(): void {
    this.getProductData(); 
  }

  // Handles image upload and stores it as a base64 string
  onFileChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      // Assign base64 string to product image field
      this.addProductData.image = reader.result as string;
      console.log(this.addProductData.image);  // Debugging: Check if image is converted correctly
    };

    // Only read the file if it exists
    if (file) {
      reader.readAsDataURL(file);
    }
  }

  // Handles form submission and emits product data to the parent component
  onSubmit() {
    let productData = {
      id: this.addProductData.id,
      name: this.addProductData.name,
      price: this.addProductData.price,
      description: this.addProductData.description,
      image: this.addProductData.image,
      quantity: 0
    };

    if (productData) { 
      // Emit data to parent component
      this.dataStorageUpdated.emit(productData);
      console.log('Product Submitted:', productData);  // Debugging: Check emitted data

      // Clear the form after submission (optional)
      this.addProductData = {
        id: null,
        name: '',
        price: null,
        description: '',
        image: ''
      };

      this.router.navigate(['/product-list']);
    }
  }

  getProductData() {
    this.productservice.editProductData$.subscribe((data) => {
      this.editproduct = data.length;

      if (data.length > 0) {
        const receivedData = data[0];
        this.addProductData.id = receivedData.id;
        this.addProductData.name = receivedData.name;
        this.addProductData.price = receivedData.price;
        this.addProductData.description = receivedData.description;
        console.log('Received data in edit:', this.addProductData);  // Debugging: Check received data
      }
    });
  }
}
