import { Component } from '@angular/core';
import { Stock } from '../stock';
import { ApiService } from '../api.service';
import { SocketService } from '../socket.service';

@Component({
  selector: 'app-watch-list',
  templateUrl: './watch-list.component.html',
  styleUrls: ['./watch-list.component.css']
})
export class WatchListComponent {
  stocks: Stock[] = [];
  index: any;
  tempArray:any = [];

  constructor(private apiService: ApiService,
    private socketService: SocketService 
    ) { }

  ngOnInit() {
    debugger;
    this.getWatchList();
    let temp: any = [];
    temp = this.tempArray;
    this.tempArray = [];
    // socket update stock price
		this.socketService.onFetchStock().subscribe((data: any) => {
      //console.log(data);
      this.stocks.forEach((item,index)=>{ if(item.SC_CODE == data.code){ 
        item.CLOSE = data.price;
        var change = Number((data.price - item.LAST).toFixed(2));
        item.TICKER_CHANGE = change;
        if (temp.length > 0) {
          let colour = change >0 ? 'green': 'red';
          if (temp [index] != colour) {
            this.index = index
          }
        }
        if (change < 0 ) {
          this.tempArray.push ('red')
        }else{
          this.tempArray.push ('green')
        }
        console.log(this.index,index)
        var percDiff =  100 * Math.abs( (data.price - item.LAST) / ( (item.LAST+data.price)/2 ) );
        item.TICKER_CHANGE_PERCENT = change > 0 ?  Number((percDiff).toFixed(2)) : - Number((percDiff).toFixed(2)) ;
      } });  
      console.log(this.tempArray)
    })

  }

  ngOnDestroy() {
    //this.socketService.unsubscribe();
    // unsubscribe socket
  }
// get watch list stocks
  getWatchList(): void {
    this.apiService.getWatchList()
      .subscribe(stocks => {
        console.log(this.stocks,stocks)
        this.stocks = stocks;
      });
  }
}
<div class="flex justify-center border-r-4 border-sky-100">
  <div class="flex flex-col">
    <div class="overflow-x-auto sm:-mx-6 lg:-mx-8">
      <div class="inline-block min-w-full py-2 sm:px-6 lg:px-8">
        <div class="overflow-hidden">
          WATCHLIST
          <table class="min-w-full text-left text-sm font-light">            
            <tbody>
              <tr class="border-b dark:border-neutral-500" *ngFor="let stock of stocks; let i = index" [ngClass]="i == index ? 'class-highlight': 'class-background'" [ngClass]="{'text-red-600': stock.TICKER_CHANGE > 0, 'text-green-600': stock.TICKER_CHANGE <= 0 }">

                <td class="whitespace-nowrap px-6 py-4" >{{stock.SC_NAME}}</td>
                <td class="whitespace-nowrap px-6 py-4" > {{stock.TICKER_CHANGE}}  ({{stock.TICKER_CHANGE_PERCENT}} %) </td>
                <td class="whitespace-nowrap px-6 py-4">{{stock.CLOSE}}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
