import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { ProductService } from 'src/app/login/services/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @Output() dataStorageUpdated = new EventEmitter<any>();

  addProductData: any = {
    id: null,
    name: '',
    price: null,
    description: '',
    image: ''
  };

  constructor(private router: Router, private productservice: ProductService) {
    this.checkLoginStatus();
  }

  checkLoginStatus() {
    if (localStorage.getItem('isLoggedIn') !== 'true') {
      this.router.navigate(['/login']);
    }
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      this.addProductData.image = reader.result as string; // Set base64 string to image property
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  }

  onSubmit() {
    if (!this.addProductData.name || !this.addProductData.price) {
      alert('Name and Price are required fields.');
      return;
    }

    this.dataStorageUpdated.emit(this.addProductData);
    console.log('Product Submitted:', this.addProductData); // Debugging
    this.router.navigate(['/product-list']);

    // Clear the form after submission (optional)
    this.addProductData = { id: null, name: '', price: null, description: '', image: '' };
  }
}
<div class="register-form">

    <form (ngSubmit)="onSubmit()">

        <div class="form-group">
            <input type="number" [(ngModel)]="addProductData.id" class="form-control" placeholder="Id" name="id">
        </div>

        <div class="form-group">
            <input type="text" class="form-control" [(ngModel)]="addProductData.name" placeholder="Name" name="name">
        </div>

        <div class="form-group">
            <input type="number" class="form-control" [(ngModel)]="addProductData.price" placeholder="Price" name="price">
        </div>

        <div class="form-group">
            <div class="form-floating">
                <textarea class="form-control" name="description" [(ngModel)]="addProductData.description" placeholder="Leave a comment here" id="floatingTextarea2" style="height: 100px"></textarea>
                <label for="floatingTextarea2">Description</label>
            </div>
        </div>

        <div class="form-group">
            <input class="form-control" name="image" (change)="onFileChange($event)" type="file" id="formFile">
        </div>

        <div class="form-group">
            <button type="submit" class="btn add-product-btn">Add Product</button>
        </div>

    </form>

</div>
