import { ProductService } from 'src/app/login/services/product.service';
import { Router } from '@angular/router';
import { Component } from '@angular/core';


@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  mAddOrEditProduct: boolean = false;


  data_storage: any[] = [];

  cart_items: any[] = [];

  cart_data: any [] = []

  added: boolean = false;

  constructor(private router : Router, private ProductService : ProductService) {

  }

  ngOnInit(): void {

    const data = localStorage.getItem('cart_data')

    if (data !== null) {
      this.data_storage = JSON.parse(data); 
    }


  }


  addCart() {
    this.router.navigate(['add-product']);
    this.mAddOrEditProduct = true;
  }

  recivedData(newDataStorage: any) {

    const updatedDataStorage = [...this.data_storage, newDataStorage];

    this.data_storage = updatedDataStorage;

    localStorage.setItem('cart_data', JSON.stringify(this.data_storage));

  }

  showDescription(item: any) {
    console.log(item)
    this.router.navigate([`description/${item.id}`])
  }

  addProduct(item: any) {

    item.added = true;

    var addQuantity = 

      {
        ...item,
        quantity: 1
      }


    const updatedDataStorage = [...this.cart_items, addQuantity];

    this.cart_items = updatedDataStorage;

    console.log(this.cart_items)

    localStorage.setItem('added_cart_data', JSON.stringify(this.cart_items));


    this.ProductService.incrementCartCount();

  }

  deleteUpdate(){

    const data = localStorage.getItem('added_cart_data')

    if (data !== null) {
      this.cart_data = JSON.parse(data);
    }


  }



  deleteProduct(item:any){
    const index = this.data_storage.indexOf(item);
    let allAddCardData = localStorage.getItem('added_cart_data');

    if(allAddCardData != null){
      let parsedDAta =  JSON.parse(allAddCardData)
      let removedAdd = parsedDAta.filter((val : any) => item.id !== val.id )
      localStorage.setItem('added_cart_data',JSON.stringify(removedAdd));
    }

    if (index !== -1) {
      this.data_storage.splice(index, 1);

      localStorage.setItem('cart_data', JSON.stringify(this.data_storage));
    }

    this.ProductService.decCartCount();

  }


  editProduct(item:any){
    const edit_product =  this.data_storage.filter((val)=> item.id == val.id)
    this.ProductService.updateEditProductData(edit_product);


    const index = this.data_storage.indexOf(item);

    if (index !== -1) {
      this.data_storage.splice(index, 1);

      localStorage.setItem('cart_data', JSON.stringify(this.data_storage));
    }


  }

}


<div class="row">



    <div class="col">



        <div style="padding: 5rem;">

            <h3 class="text-center pt-3" style="font-weight: bold;">Add Products</h3>

            <div class="card text-white" style="width: 35rem; background-color:#e6e8e7;" >

                <app-add-product (dataStorageUpdated)="recivedData($event)"></app-add-product>          


            </div>

        </div>






    </div>

    <div class="col">

        <div class="list pt-5">

            <div class="row">

                <h3 class="text-center pt-5" style="font-weight: bold;">List of Products</h3>

                <div class="col-md-6" *ngFor="let item of data_storage">


                 <div class="cart">

                    <div class="col-lg-4 col-md-6 col-sm-6" >

                        <figure class="card card-product-grid" style="background-color: #add8e6;">
                            <div class="img-wrap">
                                <img  src="{{item.image}}"  class="img-fluid" alt="No image" >
                            </div>

                            <div class="card-body">

                                <figcaption class="info-wrap border-top">
                                    <div class="price-wrap">
                                        <strong class="price">${{item.price}}</strong>
                                        <h5>{{item.name}}</h5>
                                    </div>
                                    <p class="title mb-2" >{{item.description}}</p>
                                    <!-- <a style="cursor: pointer;" (click)="showDescription(item)">More...</a> -->
                                    <a [routerLink]="['/product-description', item.id]" style="cursor: pointer;">More...</a>

                                </figcaption>

                            </div>


                            <div class="card-footer text-muted" style="display: flex;justify-content: space-between;">

                                <button *ngIf="!item.added" class="btn btn-primary" (click)="addProduct(item)" >Add to cart</button>

                                <button *ngIf="item.added" class="btn btn-success"  disabled>Added</button>

                                <button  class="btn btn-warning"  (click)="editProduct(item)" style="    color: #ebe0e0; background-color: #2a11d9; border-color: #2a11d9;">Edit</button>

                                <a class="btn btn-danger" (click)="deleteProduct(item)" style="    color: #fff; background-color: #dc3545;border-color: #dc3545;">Delete</a>
                            </div>

                        </figure>

                    </div>


                    <div>

                    </div>

                 </div>

                </div>
            </div>

        </div>
    </div>

</div>
