import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { ProductService } from 'src/app/login/services/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @Output() dataStorageUpdated = new EventEmitter<any>();

  addProductData: any = {
    id: null,
    name: '',
    price: null,
    description: '',
    image: ''
  };

  constructor(private router: Router, private productservice: ProductService) {
    this.checkLoginStatus();
  }

  checkLoginStatus() {
    if (localStorage.getItem('isLoggedIn') !== 'true') {
      this.router.navigate(['/login']);
    }
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      this.addProductData.image = reader.result as string; // Set base64 string to image property
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  }

  onSubmit() {
    if (!this.addProductData.name || !this.addProductData.price) {
      alert('Name and Price are required fields.');
      return;
    }

    this.dataStorageUpdated.emit(this.addProductData);
    console.log('Product Submitted:', this.addProductData); // Debugging
    this.router.navigate(['/product-list']);

    // Clear the form after submission (optional)
    this.addProductData = { id: null, name: '', price: null, description: '', image: '' };
  }
}
